import React, { useState } from 'react';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setResults([]);

    try {
      const response = await fetch(`http://localhost:8000/find_homes?promt=${encodeURIComponent(query)}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
      });

      if (!response.ok) {
        throw new Error(`Ошибка: ${response.status}`);
      }

      const data = await response.json();
      setResults(data); // предполагаем, что бэкенд возвращает массив объектов
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <div 
        className="header"
        style={{
          display: 'flex',
          alignItems: 'center',
          gap: '10px',
          fontSize: '20px',
          fontWeight: '600',
          color: '#333',
          padding: '15px',
          backgroundColor: 'white',
          marginBottom: '20px'
        }}
      >
        <img src="/logo.svg" alt="Логотип" className="logo" style={{ width: '70px', height: '40px' }} />
        <span>Подобранная недвижимость</span>
      </div>

      <form onSubmit={handleSubmit} style={{ marginBottom: '20px', padding: '0 15px' }}>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Введите запрос..."
          style={{ padding: '10px', width: '300px', marginRight: '10px' }}
        />
        <button type="submit" style={{ padding: '10px 20px' }}>Найти</button>
      </form>

      {loading && <p>Загрузка...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div className="results" style={{ padding: '0 15px' }}>
        {results.length > 0 && results.map((property, index) => (
          <div key={index} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
            <p><strong>Название:</strong> {property.name || '–'}</p>
            <p><strong>Адрес:</strong> {property.address || '–'}</p>
            <p><strong>Цена:</strong> {property.price || '–'}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
