import React, { useState, useRef, useEffect } from 'react';
import './PropertyListings.css';
import PropertyCard from './PropertyCard';
import AdvertisementCard from './AdvertisementCard';
import StaticMap from './StaticMap';

const PropertyListings = () => {
  const [selectedProperty, setSelectedProperty] = useState(null);
  const [mapWidth, setMapWidth] = useState(400);
  const mapSidebarRef = useRef(null);
  const isResizing = useRef(false);

  const listings = [
    {
      id: 1,
      type: "Дом",
      area: "49,9 м²",
      price: "11 000 000 Р",
      metro: "Прокшино",
      walkTime: "24 мин",
      address: "деревня Николо-Хованское, 30, Коммунарка, д. Николо-Хованское",
      source: "ЦИАН",
      sourceUrl: "#",
      hasBanner: true,
      bannerText: "Сначала ищи на Циан",
      lat: 55.5639,
      lng: 37.4839
    },
    {
      id: 2,
      type: "Дом",
      area: "112 м²",
      price: "15 495 200 Р",
      metro: "Д. Ботаково",
      walkTime: "3 мин",
      address: "КИЗ Весна-1, 2/2 ст1, КИЗ Весна-1, Троицк гор. округ, д. Ботаково",
      source: "ЦИАН",
      sourceUrl: "#",
      hasBanner: true,
      bannerText: "Сначала ищи на Циан",
      lat: 55.4567,
      lng: 37.3123
    },
    {
      id: 3,
      type: "Квартира",
      area: "65 м²",
      price: "8 500 000 Р",
      metro: "Сокольники",
      walkTime: "12 мин",
      address: "ул. Сокольническая, 15, Москва",
      source: "ЦИАН",
      sourceUrl: "#",
      hasBanner: false,
      lat: 55.7890,
      lng: 37.6789
    },
    {
      id: 4,
      type: "Квартира",
      area: "85 м²",
      price: "12 300 000 Р",
      metro: "Красные Ворота",
      walkTime: "8 мин",
      address: "ул. Мясницкая, 25, Москва",
      source: "ЦИАН",
      sourceUrl: "#",
      hasBanner: false,
      lat: 55.7654,
      lng: 37.6543
    }
  ];

  const handlePropertyClick = (property) => {
    setSelectedProperty(property);
  };

  const handleMapSizeChange = (size) => {
    setMapWidth(size);
  };

  useEffect(() => {
    const handleMouseDown = (e) => {
      if (e.target.classList.contains('resize-handle')) {
        isResizing.current = true;
        e.preventDefault();

        const startX = e.clientX;
        const startWidth = mapWidth;

        const handleMouseMove = (e) => {
          if (!isResizing.current) return;

          const deltaX = e.clientX - startX;
          const newWidth = Math.max(300, Math.min(window.innerWidth * 0.6, startWidth + deltaX));
          setMapWidth(newWidth);
        };

        const handleMouseUp = () => {
          isResizing.current = false;
          document.removeEventListener('mousemove', handleMouseMove);
          document.removeEventListener('mouseup', handleMouseUp);
        };

        document.addEventListener('mousemove', handleMouseMove);
        document.addEventListener('mouseup', handleMouseUp);
      }
    };

    document.addEventListener('mousedown', handleMouseDown);

    return () => {
      document.removeEventListener('mousedown', handleMouseDown);
    };
  }, [mapWidth]);

  return (
    <div className="property-listings">
      <div className="listings-header">
        <div className="total-count">5 826 предложений</div>
      </div>

      <div className="main-content">
        <div className="listings-container">
          {listings.map((listing) => (
            <PropertyCard 
              key={listing.id} 
              listing={listing} 
              isSelected={selectedProperty?.id === listing.id}
              onClick={() => handlePropertyClick(listing)}
            />
          ))}

          <AdvertisementCard />
        </div>

        <div 
          className="map-sidebar"
          ref={mapSidebarRef}
          style={{ width: `${mapWidth}px` }}
        >
          <div className="resize-handle"></div>
          <div className="map-width-indicator">
            {mapWidth}px
          </div>
          <StaticMap selectedProperty={selectedProperty} />
        </div>
      </div>
    </div>
  );
};

export default PropertyListings;
