import React, { useState } from 'react';
import { Static } from './Static';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [properties, setProperties] = useState([]);
  const [submitted, setSubmitted] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`http://localhost:8000/find_homes?promt=${encodeURIComponent(query)}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
      });

      if (!response.ok) {
        throw new Error(`Ошибка: ${response.status}`);
      }

      const data = await response.json();
      setProperties(data); // предполагаем, что бэкенд возвращает массив объектов
      setSubmitted(true); // скрываем форму и показываем Static
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <div 
        className="header"
        style={{
          display: 'flex',
          alignItems: 'center',
          gap: '10px',
          fontSize: '20px',
          fontWeight: '600',
          color: '#333',
          padding: '15px',
          backgroundColor: 'white',
          marginBottom: '20px'
        }}
      >
        <img src="/logo.svg" alt="Логотип" className="logo" style={{ width: '70px', height: '40px' }} />
        <span>Подобранная недвижимость</span>
      </div>

      {!submitted ? (
        <form onSubmit={handleSubmit} style={{ marginBottom: '20px', padding: '0 15px' }}>
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Введите запрос..."
            style={{ padding: '10px', width: '300px', marginRight: '10px' }}
          />
          <button type="submit" style={{ padding: '10px 20px' }}>Найти</button>
        </form>
      ) : (
        <Static properties={properties} />
      )}

      {loading && <p style={{ paddingLeft: '15px' }}>Загрузка...</p>}
      {error && <p style={{ color: 'red', paddingLeft: '15px' }}>{error}</p>}
    </div>
  );
}

export default App;
