import React, { useEffect, useRef, useState } from 'react';
import './Map2GIS.css';
import { API_CONFIG } from '../config/api';

/* global DG */

const Map2GIS = ({ selectedProperty, onMapReady }) => {
  const mapRef = useRef(null);
  const mapInstance = useRef(null);
  const [isLoaded, setIsLoaded] = useState(false);

  const initializeMap = React.useCallback(() => {
    if (mapRef.current && !mapInstance.current && window.DG) {
      try {
        console.log('Инициализация карты 2ГИС с ключом API...');
        // Инициализируем карту с центром в Москве
        mapInstance.current = DG.map(mapRef.current, {
          center: [55.7558, 37.6176], // Москва
          zoom: 10,
          key: API_CONFIG.KEY // Ключ API 2ГИС
        });

        console.log('Карта 2ГИС успешно инициализирована с ключом API');
        setIsLoaded(true);
        if (onMapReady) {
          onMapReady(mapInstance.current);
        }
      } catch (error) {
        console.error('Ошибка инициализации карты 2ГИС:', error);
      }
    }
  }, [onMapReady]);

  useEffect(() => {
    console.log('Проверка загрузки 2ГИС API...', !!window.DG);

    const handleScriptLoad = () => {
      console.log('Скрипт 2ГИС загружен');
      if (window.DG) {
        initializeMap();
      }
    };

    // Проверяем, что 2ГИС API загружен
    if (window.DG) {
      initializeMap();
    } else {
      // Ждем загрузки API
      const checkAPI = setInterval(() => {
        console.log('Ожидание загрузки 2ГИС API...', !!window.DG);
        if (window.DG) {
          clearInterval(checkAPI);
          initializeMap();
        }
      }, 100);

      // Также слушаем событие загрузки скрипта
      window.addEventListener('load', handleScriptLoad);

      return () => {
        clearInterval(checkAPI);
        window.removeEventListener('load', handleScriptLoad);
      };
    }
  }, [initializeMap]);

  useEffect(() => {
    if (selectedProperty && mapInstance.current && isLoaded) {
      // Очищаем предыдущие маркеры
      mapInstance.current.eachLayer((layer) => {
        if (layer instanceof DG.Marker) {
          mapInstance.current.removeLayer(layer);
        }
      });

      // Добавляем маркер для выбранного объекта
      const marker = DG.marker([selectedProperty.lat, selectedProperty.lng])
        .addTo(mapInstance.current);

      // Создаем всплывающее окно с информацией об объекте
      const popup = DG.popup()
        .setContent(`
          <div style="padding: 8px;">
            <h4 style="margin: 0 0 8px 0; font-size: 14px;">${selectedProperty.type}, ${selectedProperty.area}</h4>
            <p style="margin: 0 0 4px 0; font-weight: bold; color: #333;">${selectedProperty.price}</p>
            <p style="margin: 0; font-size: 12px; color: #666;">${selectedProperty.address}</p>
          </div>
        `);

      marker.bindPopup(popup);

      // Центрируем карту на выбранном объекте
      mapInstance.current.setView([selectedProperty.lat, selectedProperty.lng], 15);
    }
  }, [selectedProperty, isLoaded]);

  return (
    <div className="map-container">
      <div className="map-header">
        <h3>Карта 2ГИС</h3>
        <div style={{ fontSize: '12px', color: '#666' }}>
          {isLoaded ? 'Карта загружена' : 'Загрузка карты...'}
        </div>
        <div style={{ fontSize: '10px', color: '#999' }}>
          DG API: {window.DG ? '✓' : '✗'}
        </div>
      </div>
      <div 
        ref={mapRef} 
        className="map-2gis"
        style={{ 
          width: '100%', 
          height: '100%',
          minHeight: '400px',
          backgroundColor: '#f0f0f0'
        }}
      />
      {!isLoaded && (
        <div style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          color: '#666',
          fontSize: '14px',
          textAlign: 'center'
        }}>
          <div>Загрузка карты 2ГИС...</div>
          <div style={{ fontSize: '12px', marginTop: '8px' }}>
            Проверьте консоль для отладочной информации
          </div>
        </div>
      )}
    </div>
  );
};

export default Map2GIS;
