import React, { useState } from 'react';
import { Map2GIS } from './Map2GIS';
import { PropertyCard } from './PropertyCard';

export function Static() {
  const [formData, setFormData] = useState('');
  const [properties, setProperties] = useState([]);
  const [mapValue, setMapValue] = useState(null);

  const handleChange = (e) => setFormData(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/properties', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: formData }),
      });
      const data = await response.json();
      setProperties(data); // получаем массив квартир
    } catch (err) {
      console.error('Ошибка при отправке формы', err);
    }
  };

  const handlePropertyClick = (property) => {
    setMapValue(property);
  };

  return (
    <div className="static-container">
      <div className="properties-list scrollable">
        <form onSubmit={handleSubmit} style={{ marginBottom: '20px' }}>
          <textarea
            value={formData}
            onChange={handleChange}
            placeholder="Расскажите о себе..."
            style={{ width: '100%', height: '100px', padding: '10px', fontSize: '16px' }}
          />
          <button type="submit" style={{ marginTop: '10px', padding: '10px 20px' }}>
            Найти квартиры
          </button>
        </form>

        {properties.map((property, idx) => (
          <PropertyCard
            key={idx}
            property={property}
            onClick={() => handlePropertyClick(property)}
          />
        ))}
      </div>

      <div className="map-wrapper">
        {mapValue ? (
          <Map2GIS value={mapValue} />
        ) : (
          <p style={{ textAlign: 'center', marginTop: '20px' }}>
            Выберите квартиру, чтобы увидеть её на карте
          </p>
        )}
      </div>
    </div>
  );
}
