import React, { useEffect, useRef } from 'react';
import { load } from '@2gis/mapgl';

export const Map2GIS = ({ value }) => {
  const mapRef = useRef(null);           // объект карты
  const mapglAPIRef = useRef(null);      // API 2GIS
  const mainMarkerRef = useRef(null);    // маркер квартиры
  const infraMarkersRef = useRef([]);    // маркеры инфраструктуры

  // Создаём карту один раз
  useEffect(() => {
    if (mapRef.current) return; // карта уже создана

    load().then((mapglAPI) => {
      mapglAPIRef.current = mapglAPI;

      const mapInstance = new mapglAPI.Map('map-container', {
        center: [37.618423, 55.751244], // Москва
        zoom: 13,
        key: '8f104049-075f-4c47-817b-b8b450854d86',
      });

      mapRef.current = mapInstance;
    });

    return () => mapRef.current && mapRef.current.destroy();
  }, []);

  // Обновляем маркеры при изменении value
  useEffect(() => {
    if (!mapRef.current || !mapglAPIRef.current || !value) return;

    const mapInstance = mapRef.current;
    const mapglAPI = mapglAPIRef.current;

    // Удаляем старый главный маркер
    if (mainMarkerRef.current) {
      mainMarkerRef.current.destroy();
      mainMarkerRef.current = null;
    }

    // Удаляем старые маркеры инфраструктуры
    infraMarkersRef.current.forEach(marker => marker.destroy());
    infraMarkersRef.current = [];

    // Создаём новый главный маркер квартиры
    mainMarkerRef.current = new mapglAPI.CircleMarker(mapInstance, {
      coordinates: [value.lon, value.lat],
      radius: 10,
      color: '#ff0000', // красный
      strokeWidth: 2,
      strokeColor: '#ffffff',
    });

    // Центрируем карту на квартире
    mapInstance.setCenter([value.lon, value.lat]);

    // Добавляем маркеры инфраструктуры
    if (value.map) {
      Object.values(value.map).forEach((places) => {
        places.forEach((place) => {
          const marker = new mapglAPI.CircleMarker(mapInstance, {
            coordinates: place.coords,
            radius: 6,
            color: '#0000ff', // синие маркеры
            strokeWidth: 1,
            strokeColor: '#ffffff',
          });
          infraMarkersRef.current.push(marker);
        });
      });
    }
  }, [value]);

  return <div id="map-container" style={{ width: '100%', height: '500px' }}></div>;
};
