import React, { useState, useEffect, useRef } from 'react';
import './StaticMap2GIS.css';

const StaticMap2GIS = ({ selectedProperty, listings }) => {
  const [mapState, setMapState] = useState({
    size: '880x380',
    center: '55.75584,37.61782',
    zoom: 14
  });
  const mapContainerRef = useRef(null);

  const API_KEY = '8f104049-075f-4c47-817b-b8b450854d86';

  // –û–±–Ω–æ–≤–ª—è–µ–º —Ü–µ–Ω—Ç—Ä –∫–∞—Ä—Ç—ã –ø—Ä–∏ –≤—ã–±–æ—Ä–µ –æ–±—ä—è–≤–ª–µ–Ω–∏—è
  useEffect(() => {
    if (selectedProperty) {
      setMapState(prev => ({
        ...prev,
        center: `${selectedProperty.lat},${selectedProperty.lng}`,
        zoom: 16
      }));
    }
  }, [selectedProperty]);

  const generateMapUrl = () => {
    const { size, center, zoom } = mapState;
    return `https://static.maps.2gis.com/1.0?s=${size}&c=${center}&z=${zoom}&key=${API_KEY}`;
  };

  const handleZoomIn = () => {
    setMapState(prev => ({
      ...prev,
      zoom: Math.min(18, prev.zoom + 1)
    }));
  };

  const handleZoomOut = () => {
    setMapState(prev => ({
      ...prev,
      zoom: Math.max(1, prev.zoom - 1)
    }));
  };

  const handleReset = () => {
    setMapState({
      size: '880x380',
      center: '55.75584,37.61782',
      zoom: 14
    });
  };

  const handleSizeChange = (newSize) => {
    setMapState(prev => ({
      ...prev,
      size: newSize
    }));
  };

  const getMarkerPosition = (marker) => {
    const container = mapContainerRef.current;
    if (!container) return { left: '0%', top: '0%' };

    const [centerLat, centerLng] = mapState.center.split(',').map(Number);

    // –ü—Ä–æ—Å—Ç–∞—è –ª–∏–Ω–µ–π–Ω–∞—è –Ω–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏—è
    const deltaLng = (marker.lng - centerLng) * 10;
    const deltaLat = (centerLat - marker.lat) * 10;

    const x = 50 + deltaLng;
    const y = 50 + deltaLat;

    return {
      left: `${Math.max(0, Math.min(100, x))}%`,
      top: `${Math.max(0, Math.min(100, y))}%`
    };
  };

  return (
    <div className="static-map-2gis">
      <div className="map-header">
        <h3>–ö–∞—Ä—Ç–∞ 2–ì–ò–°</h3>
        <div className="map-controls">
          <div className="zoom-controls">
            <button onClick={handleZoomOut} disabled={mapState.zoom <= 1}>‚àí</button>
            <button onClick={handleZoomIn} disabled={mapState.zoom >= 18}>+</button>
            <button onClick={handleReset}>‚åÇ</button>
          </div>
          <div className="size-controls">
            <button onClick={() => handleSizeChange('600x300')}>–ú–∞–ª</button>
            <button onClick={() => handleSizeChange('880x380')}>–°—Ä–µ–¥</button>
            <button onClick={() => handleSizeChange('1200x500')}>–ë–æ–ª</button>
          </div>
        </div>
      </div>

      <div className="map-container" ref={mapContainerRef}>
        <img src={generateMapUrl()} alt="–ö–∞—Ä—Ç–∞ 2–ì–ò–°" className="map-image" />

        <div className="markers-overlay">
          {listings.map(marker => {
            const isSelected = selectedProperty?.id === marker.id;
            const position = getMarkerPosition(marker);

            return (
              <div
                key={marker.id}
                className={`map-marker ${isSelected ? 'selected' : ''}`}
                style={position}
                title={`${marker.type}, ${marker.price}`}
              >
                <div className="marker-pin">
                  <div className="marker-icon">{marker.type === '–î–æ–º' ? 'üè†' : 'üè¢'}</div>
                </div>
                {isSelected && (
                  <div className="marker-popup">
                    <div className="popup-content">
                      <div className="popup-title">{marker.type}</div>
                      <div className="popup-price">{marker.price}</div>
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>

      <div className="map-info">
        <div>–ú–∞—Å—à—Ç–∞–±: {mapState.zoom}</div>
        <div>–†–∞–∑–º–µ—Ä: {mapState.size}</div>
        <div>–¶–µ–Ω—Ç—Ä: {mapState.center}</div>
      </div>
    </div>
  );
};

export default StaticMap2GIS;
