// Map2GIS.jsx
import React, { useEffect, useRef } from 'react';
import { load } from '@2gis/mapgl';

export const Map2GIS = ({ property }) => {
  const mapRef = useRef(null);

  useEffect(() => {
    let mapInstance;
    load().then((mapglAPI) => {
      mapInstance = new mapglAPI.Map('map-container', {
        center: [37.618423, 55.751244], // центр Москвы
        zoom: 13,
        key: '8f104049-075f-4c47-817b-b8b450854d86',
      });
      mapRef.current = mapInstance;
    });

    return () => mapInstance && mapInstance.destroy();
  }, []);

  useEffect(() => {
    if (!mapRef.current || !property) return;

    // Удаляем старые маркеры
    if (mapRef.current.markers) {
      mapRef.current.markers.forEach(marker => marker.destroy());
    }

    // Добавляем новые маркеры
    const newMarkers = [];
    Object.values(property.map).forEach(category => {
      category.forEach(place => {
        const marker = new window.mapglAPI.Marker(mapRef.current, {
          coordinates: place.coords,
          title: place.name,
        });
        newMarkers.push(marker);
      });
    });

    mapRef.current.markers = newMarkers;

    // Центрируем карту на первом маркере
    if (newMarkers.length > 0) {
      mapRef.current.setCenter(newMarkers[0].coordinates);
    }
  }, [property]);

  return <div id="map-container" style={{ width: '100%', height: '100%' }}></div>;
};
