import React, { useState } from 'react';
import './PropertyListings.css';
import PropertyCard from './PropertyCard';
import AdvertisementCard from './AdvertisementCard';
import StaticMap2GIS from './StaticMap2GIS';

const PropertyListings = () => {
  const [selectedProperty, setSelectedProperty] = useState(null);

   const listings = [
    {
      id: 1,
      name: "Современный дом в Прокшино",
      description: "Просторный дом с садом и гаражом. Отличное расположение рядом с метро.",
      type: "Дом",
      roomsCnt: 3,
      square: "49,9 м²",
      floor: 1,
      images: [
        "https://via.placeholder.com/300x200.png?text=Дом+1",
        "https://via.placeholder.com/300x200.png?text=Дом+1+внутри"
      ],
      url: "#",
      price: "11 000 000 Р",
      metrPrice: "220 000 Р/м²",
      address: "2 кабельный проезд, 12, Москва",
      station: "Прокшино",
      lat: 55.876809,
      lng: 37.514289
    },
    {
      id: 2,
      name: "Большая квартира в Троицке",
      description: "Квартира с видом на парк. Две спальни, кухня-гостиная и балкон.",
      type: "Квартира",
      roomsCnt: 2,
      square: "112 м²",
      floor: 5,
      images: [
        "https://images.cdn-cian.ru/images/kvartira-moskva-aviacionnaya-ulica-2639360302-1.jpg",
        "https://via.placeholder.com/300x200.png?text=Квартира+1+внутри"
      ],
      url: "#",
      price: "15 495 200 Р",
      metrPrice: "138 400 Р/м²",
      address: "КИЗ Весна-1, 2/2 ст1, Троицк, Москва",
      station: "Д. Ботаково",
      lat: 55.4567,
      lng: 37.3123
    },
    {
      id: 3,
      name: "Уютный таунхаус в Москве",
      description: "Теплый таунхаус с небольшим двориком, недалеко от метро.",
      type: "Дом",
      roomsCnt: 4,
      square: "85 м²",
      floor: 2,
      images: [
        "https://via.placeholder.com/300x200.png?text=Таунхаус+1",
        "https://via.placeholder.com/300x200.png?text=Таунхаус+1+внутри"
      ],
      url: "#",
      price: "18 200 000 Р",
      metrPrice: "214 000 Р/м²",
      address: "ул. Новая, 14, Москва",
      station: "Щербинка",
      lat: 55.6453,
      lng: 37.5692
    }
  ];


  const handlePropertyClick = (property) => {
    setSelectedProperty(property);
  };

  return (
    <div className="property-listings">
      <div className="listings-header">
      </div>

      <div className="main-content">
        <div className="listings-container">
          {listings.map((listing) => (
            <PropertyCard 
              key={listing.id} 
              listing={listing} 
              isSelected={selectedProperty?.id === listing.id}
              onClick={() => handlePropertyClick(listing)}
            />
          ))}

        </div>

        <div className="map-sidebar">
          <StaticMap2GIS selectedProperty={selectedProperty} />
        </div>
      </div>
    </div>
  );
};

export default PropertyListings;
