import React, { useEffect, useRef } from 'react';
import { load } from '@2gis/mapgl';

export const Map2GIS = ({ value }) => {
  const mapRef = useRef(null);
  const markerRef = useRef(null);

  useEffect(() => {
    let mapInstance;

    load().then((mapglAPI) => {
      // создаём карту только один раз
      mapInstance = new mapglAPI.Map("map-container", {
        center: [55.31878, 25.23584],
        zoom: 13,
        key: "8f104049-075f-4c47-817b-b8b450854d86",
      });

      mapRef.current = mapInstance;
    });

    // удаляем карту при размонтировании
    return () => mapRef.current && mapRef.current.destroy();
  }, []);

  // эффект для обновления маркера при изменении value
  useEffect(() => {
    if (!mapRef.current || !value) return;

    // если маркер уже есть — удаляем
    if (markerRef.current) {
      markerRef.current.destroy();
    }

    const mapglAPI = mapRef.current.constructor; // API для создания маркера

    markerRef.current = new mapglAPI.Marker(mapRef.current, {
      coordinates: [value.lng, value.lat],
    });

    // центрируем карту на маркере
    mapRef.current.setCenter([value.lng, value.lat]);
  }, [value]);

  return <div id="map-container" style={{ width: '100%', height: '500px' }}></div>;
};
