import React, { useEffect } from 'react';
import { load } from '@2gis/mapgl';

export const Map2GIS = ({ property }) => {
    useEffect(() => {
        let map;

        load().then((mapglAPI) => {
            // Создаем карту
            map = new mapglAPI.Map('map-container', {
                center: [37.618423, 55.751244], // Центр карты (можно поставить координаты property)
                zoom: 13,
                key: '8f104049-075f-4c47-817b-b8b450854d86',
            });

            // Добавляем маркеры для всех объектов property.map
            Object.keys(property.map).forEach(category => {
                property.map[category].forEach(place => {
                    const marker = new mapglAPI.Marker(map, {
                        coordinates: [place.coords[0], place.coords[1]],
                    });

                    // Добавляем всплывающую подсказку при клике
                    marker.on('click', () => {
                        new mapglAPI.Popup(map, {
                            coordinates: [place.coords[0], place.coords[1]],
                            content: `<strong>${place.name}</strong><br/>${place.address}<br/>Категория: ${category}`,
                        });
                    });
                });
            });
        });

        return () => map && map.destroy();
    }, [property]);

    return <div id="map-container" style={{ width: '100%', height: '500px' }}></div>;
};
