import React, { useState, useEffect, useRef } from 'react';
import './StaticMap2GIS.css';

const StaticMap2GIS = ({ selectedProperty }) => {
  const [mapState, setMapState] = useState({
    size: '880x380',
    center: '55.75584,37.61782',
    zoom: 14
  });
  const mapContainerRef = useRef(null);

  const API_KEY = '8f104049-075f-4c47-817b-b8b450854d86';



  // –û–±–Ω–æ–≤–ª—è–µ–º —Ü–µ–Ω—Ç—Ä –∫–∞—Ä—Ç—ã –ø—Ä–∏ –≤—ã–±–æ—Ä–µ –æ–±—ä—è–≤–ª–µ–Ω–∏—è
  useEffect(() => {
    if (selectedProperty) {
      setMapState(prev => ({
        ...prev,
        center: `${selectedProperty.lat},${selectedProperty.lng}`,
        zoom: 16
      }));
    }
  }, [selectedProperty]);

  const generateMapUrl = () => {
    const { size, center, zoom } = mapState;
    return `https://static.maps.2gis.com/1.0?s=${size}&c=${center}&z=${zoom}&key=${API_KEY}`;
  };

  const handleZoomIn = () => {
    setMapState(prev => ({
      ...prev,
      zoom: Math.min(18, prev.zoom + 1)
    }));
  };

  const handleZoomOut = () => {
    setMapState(prev => ({
      ...prev,
      zoom: Math.max(1, prev.zoom - 1)
    }));
  };

  const handleReset = () => {
    setMapState({
      size: '880x380',
      center: '55.75584,37.61782',
      zoom: 14
    });
  };

  const handleSizeChange = (newSize) => {
    setMapState(prev => ({
      ...prev,
      size: newSize
    }));
  };

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Ä–∞—Å—á–µ—Ç–∞ –ø–æ–∑–∏—Ü–∏–∏ –º–∞—Ä–∫–µ—Ä–∞ –æ—Ç–Ω–æ—Å–∏—Ç–µ–ª—å–Ω–æ –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä–∞
  const getMarkerPosition = (marker) => {
    const container = mapContainerRef.current;
    if (!container) return { left: '0%', top: '0%' };

    const [centerLat, centerLng] = mapState.center.split(',').map(Number);
    const [mapWidth, mapHeight] = container.getBoundingClientRect().width 
      ? [container.getBoundingClientRect().width, container.getBoundingClientRect().height]
      : [880, 380];

    // –ü—Ä–æ—Å—Ç–∞—è –ª–∏–Ω–µ–π–Ω–∞—è –Ω–æ—Ä–º–∞–ª–∏–∑–∞—Ü–∏—è –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç –æ—Ç–Ω–æ—Å–∏—Ç–µ–ª—å–Ω–æ –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä–∞
    const deltaLng = (marker.lng - centerLng) * 10; // –∫–æ—ç—Ñ—Ñ–∏—Ü–∏–µ–Ω—Ç –º–∞—Å—à—Ç–∞–±–∏—Ä–æ–≤–∞–Ω–∏—è
    const deltaLat = (centerLat - marker.lat) * 10;

    const x = 50 + deltaLng; // 50% —Ü–µ–Ω—Ç—Ä
    const y = 50 + deltaLat;

    return {
      left: `${Math.max(0, Math.min(100, x))}%`,
      top: `${Math.max(0, Math.min(100, y))}%`
    };
  };

  return (
    <div className="static-map-2gis">
      <div className="map-header">
        <h3>–ö–∞—Ä—Ç–∞ 2–ì–ò–°</h3>
        <div className="map-controls">
          <div className="zoom-controls">
            <button 
              className="zoom-btn"
              onClick={handleZoomOut}
              disabled={mapState.zoom <= 1}
              title="–£–º–µ–Ω—å—à–∏—Ç—å"
            >
              ‚àí
            </button>
            <button 
              className="zoom-btn"
              onClick={handleZoomIn}
              disabled={mapState.zoom >= 18}
              title="–£–≤–µ–ª–∏—á–∏—Ç—å"
            >
              +
            </button>
            <button 
              className="zoom-btn reset-btn"
              onClick={handleReset}
              title="–°–±—Ä–æ—Å–∏—Ç—å"
            >
              ‚åÇ
            </button>
          </div>
          <div className="size-controls">
            <button 
              className="size-btn"
              onClick={() => handleSizeChange('600x300')}
              title="–ú–∞–ª–µ–Ω—å–∫–∞—è –∫–∞—Ä—Ç–∞"
            >
              –ú–∞–ª
            </button>
            <button 
              className="size-btn"
              onClick={() => handleSizeChange('880x380')}
              title="–°—Ä–µ–¥–Ω—è—è –∫–∞—Ä—Ç–∞"
            >
              –°—Ä–µ–¥
            </button>
            <button 
              className="size-btn"
              onClick={() => handleSizeChange('1200x500')}
              title="–ë–æ–ª—å—à–∞—è –∫–∞—Ä—Ç–∞"
            >
              –ë–æ–ª
            </button>
          </div>
        </div>
      </div>

      <div 
        className="map-container"
        ref={mapContainerRef}
      >
        <img 
          src={generateMapUrl()} 
          alt="–ö–∞—Ä—Ç–∞ 2–ì–ò–°"
          className="map-image"
        />

        <div className="markers-overlay">
          {propertyMarkers.map(marker => {
            const isSelected = selectedProperty?.id === marker.id;
            const position = getMarkerPosition(marker);

            return (
              <div
                key={marker.id}
                className={`map-marker ${isSelected ? 'selected' : ''}`}
                style={position}
                title={`${marker.type}, ${marker.price}`}
              >
                <div className="marker-pin">
                  <div className="marker-icon">
                    {marker.type === '–î–æ–º' ? 'üè†' : 'üè¢'}
                  </div>
                </div>
                {isSelected && (
                  <div className="marker-popup">
                    <div className="popup-content">
                      <div className="popup-title">{marker.type}</div>
                      <div className="popup-price">{marker.price}</div>
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>

      <div className="map-info">
        <div className="info-item">
          <span className="info-label">–ú–∞—Å—à—Ç–∞–±:</span>
          <span className="info-value">{mapState.zoom}</span>
        </div>
        <div className="info-item">
          <span className="info-label">–†–∞–∑–º–µ—Ä:</span>
          <span className="info-value">{mapState.size}</span>
        </div>
        <div className="info-item">
          <span className="info-label">–¶–µ–Ω—Ç—Ä:</span>
          <span className="info-value">{mapState.center}</span>
        </div>
      </div>
    </div>
  );
};

export default StaticMap2GIS;
