import React, { useState, useEffect, useRef } from 'react';
import { load } from '@2gis/mapgl';
import './MapSize.css';

// Тестовые данные
const testProperty = {
  name: "Квартира мечты",
  description: "Просторная квартира с видом на город, свежий ремонт.",
  cntrooms: 3,
  square: 85,
  floor: 5,
  images: [
    "https://images.cdn-cian.ru/images/2653186249-1.jpg",
    "https://images.cdn-cian.ru/images/2653186278-1.jpg"
  ],
  url: "https://example.com/property/1",
  price: 15000000,
  priceForMetr: 176470,
  address: "г. Москва, ул. Примерная, д. 10",
  station: "м. Парк Культуры",
  coords: [37.618423, 55.751244],
  map: {
    "Продукты": [
      { name: "Пятёрочка", address: "ул. Ленина, 5", coords: [37.618423, 55.751244] },
      { name: "Перекрёсток", address: "ул. Ленина, 10", coords: [37.620000, 55.752000] }
    ],
    "Школы": [
      { name: "Школа №12", address: "ул. Ленина, 12", coords: [37.619000, 55.753000] }
    ],
    "Детские сады": [],
    "Медицина": [
      { name: "Поликлиника №5", address: "ул. Ленина, 20", coords: [37.621000, 55.754000] }
    ],
    "Аптеки": [],
    "Спорт": [],
    "Культура": [],
    "Бары": [
      { name: "Бар Пятница", address: "ул. Ленина, 15", coords: [37.622000, 55.755000] }
    ]
  }
};

// Компонент карточки недвижимости
const PropertyCard = ({ property, onClick }) => (
  <div
    className="property-card"
    onClick={() => onClick(property)}
    style={{
      border: "1px solid #ccc",
      padding: "10px",
      marginBottom: "10px",
      cursor: "pointer"
    }}
  >
    <h3>{property.name}</h3>
    <p>{property.address}</p>
    <p>{property.price.toLocaleString()} ₽</p>
  </div>
);

const MapSize = () => {
  const mapContainerRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const mapglRef = useRef(null); // сохраняем объект mapgl
  const [selectedProperty, setSelectedProperty] = useState(null);

  // Инициализация карты
  useEffect(() => {
    let isMounted = true;

    load().then((mapgl) => {
      if (!isMounted) return;

      mapglRef.current = mapgl;

      mapInstanceRef.current = new mapgl.Map(mapContainerRef.current, {
        center: [37.618423, 55.751244],
        zoom: 13,
        key: '8f104049-075f-4c47-817b-b8b450854d86',
      });
    });

    return () => {
      isMounted = false;
      if (mapInstanceRef.current) {
        mapInstanceRef.current.destroy();
        mapInstanceRef.current = null;
        mapglRef.current = null;
      }
    };
  }, []);

  // Обновление маркеров при выборе квартиры
  useEffect(() => {
    if (!mapInstanceRef.current || !mapglRef.current || !selectedProperty) return;
    const map = mapInstanceRef.current;
    const mapgl = mapglRef.current;

    // Удаляем старые маркеры (если есть)
    map.layers?.forEach(layer => {
      if (layer.id?.startsWith('marker-')) map.removeLayer(layer.id);
    });

    // Красный маркер квартиры
    if (selectedProperty.coords) {
      new mapgl.Marker({ lngLat: selectedProperty.coords, color: 'red' }).addTo(map);
      map.setCenter(selectedProperty.coords);
    }

    // Синие маркеры инфраструктуры
    if (selectedProperty.map) {
      Object.values(selectedProperty.map).forEach(category => {
        (category || []).forEach(place => {
          if (place?.coords) {
            new mapgl.Marker({ lngLat: place.coords, color: 'blue' }).addTo(map);
          }
        });
      });
    }

  }, [selectedProperty]);

  return (
    <div className="container">
      <div className="text">
        <h1>Список объектов</h1>
        <PropertyCard property={testProperty} onClick={setSelectedProperty} />
      </div>
      <div className="map" ref={mapContainerRef} />
    </div>
  );
};

export default MapSize;
