import React, { useState, useEffect } from 'react';
import './StaticMap2GIS.css';

const StaticMap2GIS = ({ selectedProperty }) => {
  const [mapState, setMapState] = useState({
    size: '880x380',
    center: '55.75584,37.61782',
    zoom: 14
  });

  const API_KEY = '8f104049-075f-4c47-817b-b8b450854d86';

  useEffect(() => {
    if (selectedProperty) {
      setMapState(prev => ({
        ...prev,
        center: `${selectedProperty.lat},${selectedProperty.lng}`,
        zoom: 16
      }));
      console.log('✅ Выбрано объявление:', selectedProperty);
    }
  }, [selectedProperty]);

  const generateMapUrl = () => {
    const { size, center, zoom } = mapState;

    const markers = [];

    // Главный маркер объекта (красный)
    if (selectedProperty) {
      markers.push(`${selectedProperty.lng},${selectedProperty.lat},pm2blm`);
    }

    // Все дополнительные маркеры (синие)
    if (selectedProperty?.map) {
      Object.entries(selectedProperty.map).forEach(([category, places]) => {
        places.forEach(p => {
          markers.push(`${p.lng},${p.lat},pm2rdl`);
        });
      });
    }

    const markersStr = markers.join('|');

    return `https://static.maps.2gis.com/1.0?s=${size}&c=${center}&z=${zoom}&key=${API_KEY}&m=${markersStr}`;
  };

  return (
    <div className="static-map-2gis">
      <div className="map-header">
        <h3>Карта 2ГИС</h3>
      </div>

      <div className="map-container">
        <img
          src={generateMapUrl()}
          alt="Карта 2ГИС"
          className="map-image"
        />
      </div>
    </div>
  );
};

export default StaticMap2GIS;
