import React, { useEffect, useRef } from 'react';
import { load } from '@2gis/mapgl';

const MapComponent = () => {
  const mapContainerRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const mapglRef = useRef(null); // сохраняем mapgl

  useEffect(() => {
    let isMounted = true;

    load().then((mapgl) => {
      if (!isMounted) return;

      mapglRef.current = mapgl;

      const map = new mapgl.Map(mapContainerRef.current, {
        center: [37.618423, 55.751244],
        zoom: 13,
        key: '8f104049-075f-4c47-817b-b8b450854d86',
      });

      mapInstanceRef.current = map;

      // Создаем моковую метку
      const markerEl = document.createElement('div');
      markerEl.style.width = '20px';
      markerEl.style.height = '20px';
      markerEl.style.backgroundColor = 'red';
      markerEl.style.borderRadius = '50%';

      new mapgl.Marker({
        element: markerEl,
        lngLat: [37.618423, 55.751244],
      }).addTo(map);
    });

    return () => {
      isMounted = false;
      if (mapInstanceRef.current) {
        mapInstanceRef.current.destroy();
        mapInstanceRef.current = null;
        mapglRef.current = null;
      }
    };
  }, []);

  return <div ref={mapContainerRef} style={{ width: '100%', height: '500px' }} />;
};

export default MapComponent;
