import React, { useState, useRef, useEffect } from 'react';
import './StaticMap.css';

const StaticMap = ({ selectedProperty, onMapReady }) => {
  const mapRef = useRef(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [mapState, setMapState] = useState({
    center: { lat: 55.7558, lng: 37.6176 }, // –ú–æ—Å–∫–≤–∞
    zoom: 10,
    markers: [],
    scale: 1,
    offset: { x: 0, y: 0 }
  });

  // –ö–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –¥–ª—è –º–∞—Ä–∫–µ—Ä–æ–≤ –æ–±—ä—è–≤–ª–µ–Ω–∏–π
  const propertyMarkers = [
    { id: 1, lat: 55.5639, lng: 37.4839, type: "–î–æ–º", price: "11 000 000 –†" },
    { id: 2, lat: 55.4567, lng: 37.3123, type: "–î–æ–º", price: "15 495 200 –†" },
    { id: 3, lat: 55.7890, lng: 37.6789, type: "–ö–≤–∞—Ä—Ç–∏—Ä–∞", price: "8 500 000 –†" },
    { id: 4, lat: 55.7654, lng: 37.6543, type: "–ö–≤–∞—Ä—Ç–∏—Ä–∞", price: "12 300 000 –†" }
  ];

  useEffect(() => {
    // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –∫–∞—Ä—Ç—ã
    setIsLoaded(true);
    if (onMapReady) {
      onMapReady({ 
        setView: (coords, zoom) => {
          setMapState(prev => ({
            ...prev,
            center: { lat: coords[0], lng: coords[1] },
            zoom: zoom || prev.zoom
          }));
        }
      });
    }
  }, [onMapReady]);

  useEffect(() => {
    if (selectedProperty) {
      // –¶–µ–Ω—Ç—Ä–∏—Ä—É–µ–º –∫–∞—Ä—Ç—É –Ω–∞ –≤—ã–±—Ä–∞–Ω–Ω–æ–º –æ–±—ä–µ–∫—Ç–µ
      setMapState(prev => ({
        ...prev,
        center: { lat: selectedProperty.lat, lng: selectedProperty.lng },
        zoom: 15
      }));
    }
  }, [selectedProperty]);

  const handleMarkerClick = (marker) => {
    console.log('–ö–ª–∏–∫ –ø–æ –º–∞—Ä–∫–µ—Ä—É:', marker);
  };

  const handleMapClick = (e) => {
    const rect = mapRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // –ü—Ä–æ—Å—Ç–æ–µ –ª–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ –∫–ª–∏–∫–∞ –ø–æ –∫–∞—Ä—Ç–µ
