import React, { useState, useEffect, useRef } from 'react';
import './StaticMap2GIS.css';

const StaticMap2GIS = ({ selectedProperty }) => {
  const [mapState, setMapState] = useState({
    size: '880x380',
    center: '55.75584,37.61782',
    zoom: 14
  });
  const [containerSize, setContainerSize] = useState({ width: 880, height: 380 });
  const mapContainerRef = useRef(null);

  const API_KEY = '8f104049-075f-4c47-817b-b8b450854d86';

  useEffect(() => {
    if (selectedProperty) {
      setMapState(prev => ({
        ...prev,
        center: `${selectedProperty.lat},${selectedProperty.lng}`,
        zoom: 16
      }));
    }
  }, [selectedProperty]);

  // –û–±–Ω–æ–≤–ª—è–µ–º —Ä–∞–∑–º–µ—Ä—ã –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä–∞
  useEffect(() => {
    const updateSize = () => {
      if (mapContainerRef.current) {
        const { width, height } = mapContainerRef.current.getBoundingClientRect();
        setContainerSize({ width, height });
      }
    };
    updateSize();
    window.addEventListener('resize', updateSize);
    return () => window.removeEventListener('resize', updateSize);
  }, []);

  // –ü—Ä–æ–µ–∫—Ü–∏—è –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç –≤ –ø—Ä–æ—Ü–µ–Ω—Ç—ã
  const lonToX = (lon) => (lon + 180) / 360;
  const latToY = (lat) => {
    const rad = lat * Math.PI / 180;
    return (1 - Math.log(Math.tan(rad) + 1 / Math.cos(rad)) / Math.PI) / 2;
  };

  const getMarkerPosition = (marker) => {
    const { width, height } = containerSize;
    const zoom = mapState.zoom;
    const [centerLat, centerLng] = mapState.center.split(',').map(Number);

    const centerX = lonToX(centerLng);
    const centerY = latToY(centerLat);

    const markerX = lonToX(marker.lng);
    const markerY = latToY(marker.lat);

    const scale = 256 * Math.pow(2, zoom);
    const dx = (markerX - centerX) * scale;
    const dy = (markerY - centerY) * scale;

    // –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç—ã –≤ –ø—Ä–æ—Ü–µ–Ω—Ç–∞—Ö
    const leftPercent = ((width / 2 + dx) / width) * 100;
    const topPercent = ((height / 2 + dy) / height) * 100;

    return {
      left: `${Math.max(0, Math.min(100, leftPercent))}%`,
      top: `${Math.max(0, Math.min(100, topPercent))}%`
    };
  };

  const generateMapUrl = () => {
    const { size, center, zoom } = mapState;
    return `https://static.maps.2gis.com/1.0?s=${size}&c=${center}&z=${zoom}&key=${API_KEY}`;
  };

  return (
    <div className="static-map-2gis">
      <div className="map-header">
        <h3>–ö–∞—Ä—Ç–∞ 2–ì–ò–°</h3>
      </div>

      <div className="map-container" ref={mapContainerRef}>
        <img src={generateMapUrl()} alt="–ö–∞—Ä—Ç–∞ 2–ì–ò–°" className="map-image" />

        {/* –ì–ª–∞–≤–Ω—ã–π –º–∞—Ä–∫–µ—Ä */}
        {selectedProperty && (
          <div
            className="map-marker selected"
            style={getMarkerPosition(selectedProperty)}
            title={`${selectedProperty.type}, ${selectedProperty.price}`}
          >
            <div className="marker-pin">
              <div className="marker-icon">
                {selectedProperty.type === '–î–æ–º' ? 'üè†' : 'üè¢'}
              </div>
            </div>
          </div>
        )}

        {/* –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –º–∞—Ä–∫–µ—Ä—ã */}
        {selectedProperty?.map &&
          Object.entries(selectedProperty.map).map(([category, places]) =>
            places.map((place, idx) => (
              <div
                key={`${category}-${idx}`}
                className="map-marker place"
                style={getMarkerPosition(place)}
                title={`${place.name} (${category})`}
              >
                <div className="marker-pin">
                  <div className="marker-icon">üìç</div>
                </div>
              </div>
            ))
          )}
      </div>
    </div>
  );
};

export default StaticMap2GIS;
