import React, { useState, useRef, useEffect } from 'react';
import './StaticMap.css';

const StaticMap = ({ selectedProperty, onMapReady }) => {
  const mapRef = useRef(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [mapState, setMapState] = useState({
    center: { lat: 55.7558, lng: 37.6176 }, // Москва
    zoom: 10,
    markers: [],
    scale: 1,
    offset: { x: 0, y: 0 }
  });

  // Координаты для маркеров объявлений
  const propertyMarkers = [
    { id: 1, lat: 55.5639, lng: 37.4839, type: "Дом", price: "11 000 000 Р" },
    { id: 2, lat: 55.4567, lng: 37.3123, type: "Дом", price: "15 495 200 Р" },
    { id: 3, lat: 55.7890, lng: 37.6789, type: "Квартира", price: "8 500 000 Р" },
    { id: 4, lat: 55.7654, lng: 37.6543, type: "Квартира", price: "12 300 000 Р" }
  ];

  useEffect(() => {
    // Инициализация карты
    setIsLoaded(true);
    if (onMapReady) {
      onMapReady({ 
        setView: (coords, zoom) => {
          setMapState(prev => ({
            ...prev,
            center: { lat: coords[0], lng: coords[1] },
            zoom: zoom || prev.zoom
          }));
        }
      });
    }
  }, [onMapReady]);

  useEffect(() => {
    if (selectedProperty) {
      // Центрируем карту на выбранном объекте
      setMapState(prev => ({
        ...prev,
        center: { lat: selectedProperty.lat, lng: selectedProperty.lng },
        zoom: 15
      }));
    }
  }, [selectedProperty]);

  const handleMarkerClick = (marker) => {
    console.log('Клик по маркеру:', marker);
  };

  const handleMapClick = (e) => {
    const rect = mapRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Простое логирование клика по карте
    console.log('Клик по карте:', { x, y });
  };

  const handleWheel = (e) => {
    e.preventDefault();
    const delta = e.deltaY > 0 ? -0.1 : 0.1;
    const newScale = Math.max(0.5, Math.min(3, mapState.scale + delta));

    setMapState(prev => ({
      ...prev,
      scale: newScale,
      zoom: Math.round(10 + (newScale - 1) * 5)
    }));
  };

  const handleMouseDown = (e) => {
    if (e.button !== 0) return; // Только левая кнопка мыши

    const startX = e.clientX - mapState.offset.x;
    const startY = e.clientY - mapState.offset.y;

    const handleMouseMove = (e) => {
      setMapState(prev => ({
        ...prev,
        offset: {
          x: e.clientX - startX,
          y: e.clientY - startY
        }
      }));
    };

    const handleMouseUp = () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
  };

  const handleZoomIn = () => {
    setMapState(prev => ({
      ...prev,
      scale: Math.min(3, prev.scale + 0.2),
      zoom: Math.min(18, prev.zoom + 1)
    }));
  };

  const handleZoomOut = () => {
    setMapState(prev => ({
      ...prev,
      scale: Math.max(0.5, prev.scale - 0.2),
      zoom: Math.max(1, prev.zoom - 1)
    }));
  };

  const handleReset = () => {
    setMapState(prev => ({
      ...prev,
      scale: 1,
      zoom: 10,
      offset: { x: 0, y: 0 }
    }));
  };

  return (
    <div className="map-container">
      <div className="map-header">
        <h3>Карта</h3>
        <div style={{ fontSize: '12px', color: '#666' }}>
          {isLoaded ? 'Карта загружена' : 'Загрузка карты...'}
        </div>
      </div>
      <div 
        ref={mapRef} 
        className="static-map"
        onClick={handleMapClick}
        onWheel={handleWheel}
        onMouseDown={handleMouseDown}
        style={{
          transform: `scale(${mapState.scale}) translate(${mapState.offset.x / mapState.scale}px, ${mapState.offset.y / mapState.scale}px)`,
          transformOrigin: 'center center'
        }}
      >
        {/* Фон карты */}
        <div className="map-background">
          {/* Сетка карты */}
          <div className="map-grid">
            {Array.from({ length: 20 }, (_, i) => (
              <div key={`h-${i}`} className="grid-line horizontal" style={{ top: `${i * 5}%` }} />
            ))}
            {Array.from({ length: 20 }, (_, i) => (
              <div key={`v-${i}`} className="grid-line vertical" style={{ left: `${i * 5}%` }} />
            ))}
          </div>

          {/* Дороги */}
          <div className="map-roads">
            {/* Главные дороги */}
            <div className="road main-road" style={{ top: '30%', left: '0%', width: '100%', height: '3px' }} />
            <div className="road main-road" style={{ top: '60%', left: '0%', width: '100%', height: '3px' }} />
            <div className="road main-road" style={{ top: '0%', left: '25%', width: '3px', height: '100%' }} />
            <div className="road main-road" style={{ top: '0%', left: '75%', width: '3px', height: '100%' }} />

            {/* Второстепенные дороги */}
            <div className="road secondary-road" style={{ top: '15%', left: '0%', width: '100%', height: '1px' }} />
            <div className="road secondary-road" style={{ top: '45%', left: '0%', width: '100%', height: '1px' }} />
            <div className="road secondary-road" style={{ top: '75%', left: '0%', width: '100%', height: '1px' }} />
            <div className="road secondary-road" style={{ top: '0%', left: '12%', width: '1px', height: '100%' }} />
            <div className="road secondary-road" style={{ top: '0%', left: '37%', width: '1px', height: '100%' }} />
            <div className="road secondary-road" style={{ top: '0%', left: '62%', width: '1px', height: '100%' }} />
            <div className="road secondary-road" style={{ top: '0%', left: '87%', width: '1px', height: '100%' }} />
          </div>

          {/* Здания и кварталы */}
          <div className="map-buildings">
            {/* Жилые кварталы */}
            <div className="building residential" style={{ top: '20%', left: '10%', width: '8%', height: '8%' }} />
            <div className="building residential" style={{ top: '35%', left: '15%', width: '6%', height: '6%' }} />
            <div className="building residential" style={{ top: '50%', left: '20%', width: '7%', height: '7%' }} />
            <div className="building residential" style={{ top: '25%', left: '30%', width: '5%', height: '5%' }} />
            <div className="building residential" style={{ top: '40%', left: '35%', width: '6%', height: '6%' }} />
            <div className="building residential" style={{ top: '55%', left: '40%', width: '8%', height: '8%' }} />

            {/* Коммерческие здания */}
            <div className="building commercial" style={{ top: '15%', left: '50%', width: '10%', height: '6%' }} />
            <div className="building commercial" style={{ top: '30%', left: '55%', width: '8%', height: '8%' }} />
            <div className="building commercial" style={{ top: '45%', left: '60%', width: '7%', height: '5%' }} />

            {/* Промышленные здания */}
            <div className="building industrial" style={{ top: '70%', left: '10%', width: '12%', height: '10%' }} />
            <div className="building industrial" style={{ top: '75%', left: '25%', width: '8%', height: '8%' }} />
          </div>

          {/* Парки и зеленые зоны */}
          <div className="map-parks">
            <div className="park" style={{ top: '5%', left: '5%', width: '15%', height: '12%' }} />
            <div className="park" style={{ top: '80%', left: '60%', width: '20%', height: '15%' }} />
            <div className="park" style={{ top: '40%', left: '80%', width: '12%', height: '18%' }} />
          </div>

          {/* Водные объекты */}
          <div className="map-water">
            <div className="water river" style={{ top: '10%', left: '0%', width: '100%', height: '2%' }} />
            <div className="water lake" style={{ top: '65%', left: '70%', width: '15%', height: '12%' }} />
            <div className="water pond" style={{ top: '25%', left: '45%', width: '8%', height: '6%' }} />
          </div>

          {/* Маркеры объявлений */}
          {propertyMarkers.map((marker) => (
            <div
              key={marker.id}
              className={`map-marker ${selectedProperty?.id === marker.id ? 'selected' : ''}`}
              style={{
                left: `${((marker.lng - 37.0) / 1.0) * 100}%`,
                top: `${((55.9 - marker.lat) / 0.5) * 100}%`
              }}
              onClick={(e) => {
                e.stopPropagation();
                handleMarkerClick(marker);
              }}
            >
              <div className="marker-pin">
                <div className="marker-icon">
                  {marker.type === 'Дом' ? '🏠' : '🏢'}
                </div>
              </div>
              {selectedProperty?.id === marker.id && (
                <div className="marker-popup">
                  <div className="popup-content">
                    <div className="popup-title">{marker.type}</div>
                    <div className="popup-price">{marker.price}</div>
                    <div className="popup-coords">
                      {marker.lat.toFixed(4)}, {marker.lng.toFixed(4)}
                    </div>
                  </div>
                </div>
              )}
            </div>
          ))}

          {/* Центр карты */}
          <div 
            className="map-center"
            style={{
              left: `${((mapState.center.lng - 37.0) / 1.0) * 100}%`,
              top: `${((55.9 - mapState.center.lat) / 0.5) * 100}%`
            }}
          />
        </div>

        {/* Элементы управления картой */}
        <div className="map-controls">
          <div className="zoom-controls">
            <button 
              className="zoom-btn"
              onClick={handleZoomIn}
              disabled={mapState.scale >= 3}
              title="Увеличить"
            >
              +
            </button>
            <button 
              className="zoom-btn"
              onClick={handleZoomOut}
              disabled={mapState.scale <= 0.5}
              title="Уменьшить"
            >
              −
            </button>
            <button 
              className="zoom-btn reset-btn"
              onClick={handleReset}
              title="Сбросить масштаб"
            >
              ⌂
            </button>
          </div>
        </div>

        {/* Информация о масштабе */}
        <div className="map-info">
          <div className="zoom-level">Масштаб: {mapState.zoom}</div>
          <div className="scale-level">Увеличение: {Math.round(mapState.scale * 100)}%</div>
          <div className="coordinates">
            {mapState.center.lat.toFixed(4)}, {mapState.center.lng.toFixed(4)}
          </div>
        </div>
      </div>
    </div>
  );
};

export default StaticMap;
