import React, { useEffect, useRef } from 'react';
import { load } from '@2gis/mapgl';

export const Map2GIS = ({ value }) => {
  const mapRef = useRef(null);
  const markerRef = useRef(null);

  // Создание карты один раз
  useEffect(() => {
    load().then((mapglAPI) => {
      const mapInstance = new mapglAPI.Map('map-container', {
        center: [37.618423, 55.751244], // центр Москвы [lng, lat]
        zoom: 13,
        key: '8f104049-075f-4c47-817b-b8b450854d86',
      });

      mapRef.current = mapInstance;
    });

    return () => mapRef.current && mapRef.current.destroy();
  }, []);

  // Обновление маркера при изменении value
  useEffect(() => {
    if (!mapRef.current || !value) return;

    const mapInstance = mapRef.current;

    // Удаляем старый маркер
    if (markerRef.current) {
      markerRef.current.destroy();
    }

    const Marker = mapInstance.constructor.Marker;
    markerRef.current = new Marker(mapInstance, {
      coordinates: [value.lon, value.lat], // [lng, lat]
    });

    // Центрируем карту на новом маркере
    mapInstance.setCenter([value.lon, value.lat]);
  }, [value]);

  return <div id="map-container" style={{ width: '100%', height: '500px' }}></div>;
};
