import React, { useEffect, useRef } from 'react';
import { load } from '@2gis/mapgl';

const MapComponent = () => {
  const mapContainerRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const mapLabel = useRef(null);

  useEffect(() => {
    let isMounted = true;

    load().then((mapgl) => {
      if (!isMounted) return;

      mapInstanceRef.current = new mapgl.Map(mapContainerRef.current, {
        center: [37.618423, 55.751244],
        zoom: 13,
        key: '8f104049-075f-4c47-817b-b8b450854d86',
      });
    });

    mapLabel.current = new mapgl.Marker(mapInstanceRef.current,{
      coordinates: [37.618423, 55.751244],
    })

    return () => {
      isMounted = false;
      if (mapInstanceRef.current) {
        mapInstanceRef.current.destroy();
        mapInstanceRef.current = null;
      }
    };
  }, []);

  return <div ref={mapContainerRef} style={{ width: '100%', height: '500px' }} />;
};

export default MapComponent;
