import React, { useEffect, useRef } from 'react';
import { load } from '@2gis/mapgl';

export const Map2GIS = ({ value }) => {
  const mapRef = useRef(null);
  const mapglAPIRef = useRef(null);
  const mainMarkerRef = useRef(null);
  const infraMarkersRef = useRef([]);

  // Создаем карту один раз
  useEffect(() => {
    let isMounted = true;

    load().then((mapglAPI) => {
      if (!isMounted) return;

      mapglAPIRef.current = mapglAPI;

      mapRef.current = new mapglAPI.Map('map-container', {
        center: [37.618423, 55.751244], // Москва
        zoom: 13,
        key: '8f104049-075f-4c47-817b-b8b450854d86',
      });
    });

    return () => {
      isMounted = false;
      mapRef.current && mapRef.current.destroy();
    };
  }, []);

  // Обновление маркеров — только если карта готова
  useEffect(() => {
    if (!mapRef.current || !mapglAPIRef.current || !value) return;

    const mapInstance = mapRef.current;
    const mapglAPI = mapglAPIRef.current;

    // Удаляем старый главный маркер
    if (mainMarkerRef.current) {
      mainMarkerRef.current.destroy();
    }

    // Удаляем маркеры инфраструктуры
    infraMarkersRef.current.forEach(marker => marker.destroy());
    infraMarkersRef.current = [];

    mainMarkerRef.current = new mapglAPI.Marker(mapInstance, {
      coordinates: [value.lon, value.lat],
      color: '#ff0000',
      icon: '/home.svg', // путь относительно public
      size: [40,40]

    });

    mapInstance.setCenter([value.lon, value.lat]);

    if (value.map) {
      Object.values(value.map).forEach((places) => {
        places.forEach((place) => {
          const marker = new mapglAPI.Marker(mapInstance, {
            coordinates: place.coords,
            color: '#0000ff',
          });
          infraMarkersRef.current.push(marker);
        });
      });
    }
  }, [value]);

  return <div id="map-container" style={{ width: '100%', height: '500px' }}></div>;
};

const getIconByKey = (key) => {
  const iconsMap = {
    'Продукты': '/products.svg',
    'Школы': '/school.svg',
    'Детские сады': '/kindergarten.svg',
    'Медицина': '/hospital.svg',
    'Аптеки': '/farma.svg',
    'Спорт': '/sport.svg',
    'Культура': '/culture.svg', 
    'Бары': '/bar.svg',
    'Жилье': '/home.svg', 
  };

  return iconsMap[key];
};
