import React, { useEffect } from 'react';
import { load } from '@2gis/mapgl';

export const Map2GIS = () => {
    // Массив координат для маркеров
    const markers = [
        [55.31878, 25.23584],
        [55.32000, 25.23700],
        [55.31700, 25.23850],
    ];

    useEffect(() => {
        let map;

        load().then((mapglAPI) => {
            // Создаем карту
            map = new mapglAPI.Map('map-container', {
                center: [55.31878, 25.23584],
                zoom: 13,
                key: '8f104049-075f-4c47-817b-b8b450854d86',
            });

            // Создаем маркеры
            markers.forEach(coords => {
                new mapglAPI.Marker(map, {
                    coordinates: coords,
                });
            });
        });

        // Удаляем карту при размонтировании
        return () => map && map.destroy();
    }, []);

    return <div id="map-container" style={{ width: '100%', height: '100%' }}></div>;
};
