import React, { useEffect } from "react";
import { load } from "2gis-maps"; // предполагаю, что используешь 2GIS JS API

export const Map2GIS = ({ value }) => {
  useEffect(() => {
    let map;
    let marker;

    load().then((mapglAPI) => {
      // Создаем карту, если она еще не создана
      map = new mapglAPI.Map("map-container", {
        center: value ? [value.lng, value.lat] : [55.31878, 25.23584],
        zoom: 13,
        key: "8f104049-075f-4c47-817b-b8b450854d86",
      });

      // Если есть value, создаем маркер
      if (value) {
        marker = new mapglAPI.Marker(map, {
          coordinates: [value.lng, value.lat],
        });
      }
    });

    // При размонтировании карты
    return () => map && map.destroy();
  }, [value]); // пересоздаем карту при изменении value

  return <div id="map-container" style={{ width: "100%", height: "500px" }}></div>;
};
