import React, { useState, useEffect } from 'react';
import './StaticMap2GIS.css';

const StaticMap2GIS = ({ selectedProperty }) => {
  const [mapState, setMapState] = useState({
    size: '880x380',
    center: '55.75584,37.61782',
    zoom: 14
  });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [mapOffset, setMapOffset] = useState({ x: 0, y: 0 });

  const API_KEY = '8f104049-075f-4c47-817b-b8b450854d86';

  const propertyMarkers = [
    { id: 1, lat: 55.5639, lng: 37.4839, type: "–î–æ–º", price: "11 000 000 –†" },
    { id: 2, lat: 55.4567, lng: 37.3123, type: "–î–æ–º", price: "15 495 200 –†" },
    { id: 3, lat: 55.7890, lng: 37.6789, type: "–ö–≤–∞—Ä—Ç–∏—Ä–∞", price: "8 500 000 –†" },
    { id: 4, lat: 55.7654, lng: 37.6543, type: "–ö–≤–∞—Ä—Ç–∏—Ä–∞", price: "12 300 000 –†" }
  ];

  // –û–±–Ω–æ–≤–ª—è–µ–º —Ü–µ–Ω—Ç—Ä –∫–∞—Ä—Ç—ã –ø—Ä–∏ –≤—ã–±–æ—Ä–µ –æ–±—ä—è–≤–ª–µ–Ω–∏—è
  useEffect(() => {
    if (selectedProperty) {
      setMapState(prev => ({
        ...prev,
        center: `${selectedProperty.lat},${selectedProperty.lng}`,
        zoom: 16
      }));
    }
  }, [selectedProperty]);

  const generateMapUrl = () => {
    const { size, center, zoom } = mapState;
    return `https://static.maps.2gis.com/1.0?s=${size}&c=${center}&z=${zoom}&key=${API_KEY}`;
  };

  const handleZoomIn = () => {
    setMapState(prev => ({
      ...prev,
      zoom: Math.min(18, prev.zoom + 1)
    }));
  };

  const handleZoomOut = () => {
    setMapState(prev => ({
      ...prev,
      zoom: Math.max(1, prev.zoom - 1)
    }));
  };

  const handleReset = () => {
    setMapState({
      size: '880x380',
      center: '55.75584,37.61782',
      zoom: 14
    });
    setMapOffset({ x: 0, y: 0 });
  };

  const handleSizeChange = (newSize) => {
    setMapState(prev => ({
      ...prev,
      size: newSize
    }));
  };

  const handleMouseDown = (e) => {
    if (e.button === 0) { // –¢–æ–ª—å–∫–æ –ª–µ–≤–∞—è –∫–Ω–æ–ø–∫–∞ –º—ã—à–∏
      setIsDragging(true);
      setDragStart({ x: e.clientX, y: e.clientY });
      e.preventDefault();
    }
  };

  const handleMouseMove = (e) => {
    if (isDragging) {
      const deltaX = e.clientX - dragStart.x;
      const deltaY = e.clientY - dragStart.y;

      setMapOffset(prev => ({
        x: prev.x + deltaX,
        y: prev.y + deltaY
      }));

      setDragStart({ x: e.clientX, y: e.clientY });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleMouseLeave = () => {
    setIsDragging(false);
  };

  useEffect(() => {
    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
      document.addEventListener('mouseleave', handleMouseLeave);
    }

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
      document.removeEventListener('mouseleave', handleMouseLeave);
    };
  }, [isDragging, dragStart]);

  return (
    <div className="static-map-2gis">
      <div className="map-header">
        <h3>–ö–∞—Ä—Ç–∞ 2–ì–ò–°</h3>
        <div className="map-controls">
          <div className="zoom-controls">
            <button 
              className="zoom-btn"
              onClick={handleZoomOut}
              disabled={mapState.zoom <= 1}
              title="–£–º–µ–Ω—å—à–∏—Ç—å"
            >
              ‚àí
            </button>
            <button 
              className="zoom-btn"
              onClick={handleZoomIn}
              disabled={mapState.zoom >= 18}
              title="–£–≤–µ–ª–∏—á–∏—Ç—å"
            >
              +
            </button>
            <button 
              className="zoom-btn reset-btn"
              onClick={handleReset}
              title="–°–±—Ä–æ—Å–∏—Ç—å"
            >
              ‚åÇ
            </button>
          </div>
          <div className="size-controls">
            <button 
              className="size-btn"
              onClick={() => handleSizeChange('600x300')}
              title="–ú–∞–ª–µ–Ω—å–∫–∞—è –∫–∞—Ä—Ç–∞"
            >
              –ú–∞–ª
            </button>
            <button 
              className="size-btn"
              onClick={() => handleSizeChange('880x380')}
              title="–°—Ä–µ–¥–Ω—è—è –∫–∞—Ä—Ç–∞"
            >
              –°—Ä–µ–¥
            </button>
            <button 
              className="size-btn"
              onClick={() => handleSizeChange('1200x500')}
              title="–ë–æ–ª—å—à–∞—è –∫–∞—Ä—Ç–∞"
            >
              –ë–æ–ª
            </button>
          </div>
        </div>
      </div>

      <div 
        className={`map-container ${isDragging ? 'dragging' : ''}`}
        onMouseDown={handleMouseDown}
        style={{
          transform: `translate(${mapOffset.x}px, ${mapOffset.y}px)`,
          cursor: isDragging ? 'grabbing' : 'grab'
        }}
      >
        <img 
          src={generateMapUrl()} 
          alt="–ö–∞—Ä—Ç–∞ 2–ì–ò–°"
          className="map-image"
        />

        {/* –ú–∞—Ä–∫–µ—Ä—ã –æ–±—ä—è–≤–ª–µ–Ω–∏–π –ø–æ–≤–µ—Ä—Ö –∫–∞—Ä—Ç—ã */}
        <div className="markers-overlay">
          {propertyMarkers.map((marker) => {
            // –ü—Ä–æ—Å—Ç–æ–µ –ø–æ–∑–∏—Ü–∏–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ –º–∞—Ä–∫–µ—Ä–æ–≤ (–≤ —Ä–µ–∞–ª—å–Ω–æ–º –ø—Ä–∏–ª–æ–∂–µ–Ω–∏–∏ –Ω—É–∂–Ω—ã —Ç–æ—á–Ω—ã–µ —Ä–∞—Å—á–µ—Ç—ã)
            const isSelected = selectedProperty?.id === marker.id;
            const x = 20 + (marker.lng - 37.0) * 200; // –ü—Ä–∏–º–µ—Ä–Ω–æ–µ –ø–æ–∑–∏—Ü–∏–æ–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ
            const y = 20 + (55.9 - marker.lat) * 200;

            return (
              <div
                key={marker.id}
                className={`map-marker ${isSelected ? 'selected' : ''}`}
                style={{
                  left: `${Math.max(0, Math.min(100, x))}%`,
                  top: `${Math.max(0, Math.min(100, y))}%`
                }}
                title={`${marker.type}, ${marker.price}`}
              >
                <div className="marker-pin">
                  <div className="marker-icon">
                    {marker.type === '–î–æ–º' ? 'üè†' : 'üè¢'}
                  </div>
                </div>
                {isSelected && (
                  <div className="marker-popup">
                    <div className="popup-content">
                      <div className="popup-title">{marker.type}</div>
                      <div className="popup-price">{marker.price}</div>
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>

      <div className="map-info">
        <div className="info-item">
          <span className="info-label">–ú–∞—Å—à—Ç–∞–±:</span>
          <span className="info-value">{mapState.zoom}</span>
        </div>
        <div className="info-item">
          <span className="info-label">–†–∞–∑–º–µ—Ä:</span>
          <span className="info-value">{mapState.size}</span>
        </div>
        <div className="info-item">
          <span className="info-label">–¶–µ–Ω—Ç—Ä:</span>
          <span className="info-value">{mapState.center}</span>
        </div>
      </div>
    </div>
  );
};

export default StaticMap2GIS;
