import React, { useState, useEffect, useRef } from 'react';
import { load } from '@2gis/mapgl';
import './MapSize.css';

const testProperty = {
  name: "Квартира мечты",
  coords: [37.618423, 55.751244],
  address: "г. Москва, ул. Примерная, д. 10",
  price: 15000000,
  map: {
    "Продукты": [
      { name: "Пятёрочка", address: "ул. Ленина, 5", coords: [37.618423, 55.751244] },
      { name: "Перекрёсток", address: "ул. Ленина, 10", coords: [37.620000, 55.752000] }
    ]
  }
};

const PropertyCard = ({ property, onClick }) => (
  <div onClick={() => onClick(property)} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px', cursor: 'pointer' }}>
    <h3>{property.name}</h3>
    <p>{property.address}</p>
    <p>{property.price.toLocaleString()} ₽</p>
  </div>
);

const MapSize = () => {
  const mapContainerRef = useRef(null);
  const mapInstanceRef = useRef(null);
  const mapglRef = useRef(null);
  const [selectedProperty, setSelectedProperty] = useState(null);

  useEffect(() => {
    load().then((mapgl) => {
      mapglRef.current = mapgl;

      mapInstanceRef.current = new mapgl.Map(mapContainerRef.current, {
        center: [37.618423, 55.751244],
        zoom: 13,
        key: '8f104049-075f-4c47-817b-b8b450854d86',
      });
    });

    return () => {
      if (mapInstanceRef.current) mapInstanceRef.current.destroy();
      mapInstanceRef.current = null;
      mapglRef.current = null;
    };
  }, []);

  useEffect(() => {
    if (!mapInstanceRef.current || !mapglRef.current || !selectedProperty) return;

    const map = mapInstanceRef.current;
    const mapgl = mapglRef.current;

    // Очистка старых маркеров
    map.layers?.forEach(layer => {
      if (layer.id?.startsWith('marker-')) map.removeLayer(layer.id);
    });

    // Красный маркер квартиры
    if (selectedProperty.coords) {
      const markerEl = document.createElement('div');
      markerEl.style.backgroundColor = 'red';
      markerEl.style.width = '20px';
      markerEl.style.height = '20px';
      markerEl.style.borderRadius = '50%';

      new mapgl.Marker({ element: markerEl, lngLat: selectedProperty.coords }).addTo(map);
      map.setCenter(selectedProperty.coords);
    }

    // Синие маркеры инфраструктуры
    if (selectedProperty.map) {
      Object.values(selectedProperty.map).forEach(category => {
        (category || []).forEach(place => {
          if (place?.coords) {
            const el = document.createElement('div');
            el.style.backgroundColor = 'blue';
            el.style.width = '15px';
            el.style.height = '15px';
            el.style.borderRadius = '50%';

            new mapgl.Marker({ element: el, lngLat: place.coords }).addTo(map);
          }
        });
      });
    }

  }, [selectedProperty]);

  return (
    <div className="container">
      <div className="text">
        <h1>Список объектов</h1>
        <PropertyCard property={testProperty} onClick={setSelectedProperty} />
      </div>
      <div className="map" ref={mapContainerRef} />
    </div>
  );
};

export default MapSize;
