import React, { useState } from 'react';
import './PropertyListings.css';
import PropertyCard from './PropertyCard';
import AdvertisementCard from './AdvertisementCard';
import StaticMap2GIS from './StaticMap2GIS';

const PropertyListings = () => {
  const [selectedProperty, setSelectedProperty] = useState(null);

  const listings = [
    {
      id: 1,
      name: "Современный дом в Прокшино",
      description: "Просторный дом с садом и гаражом. Отличное расположение рядом с метро.",
      type: "Дом",
      roomsCnt: 3,
      square: "49,9 м²",
      floor: 1,
      images: [
        "https://images.cdn-cian.ru/images/kvartira-moskva-aviacionnaya-ulica-2639360302-1.jpg"
      ],
      url: "#",
      price: "11 000 000 Р",
      metrPrice: "220 000 Р/м²",
      address: "2 кабельный проезд, 4, Москва",
      station: "Прокшино",
      lat: 55.749284,
      lng: 37.714151,

      map: {
        "Продукты": [
          { name: "Пятёрочка", address: "ул. Южная, 15", lat: 55.749219, lng: 37.717894 }        ],
        "Школы": [
          { name: "Школа №2120", address: "ул. Академическая, 10", lat: 55.7479, lng: 37.7132 }
        ],
        "Детские сады": [
          { name: "Детсад №1552", address: "ул. Новая, 3", lat: 55.7487, lng: 37.7151 }
        ],
        "Медицина": [
          { name: "Поликлиника №5", address: "ул. Здоровья, 2", lat: 55.7499, lng: 37.7115 }
        ],
        "Аптеки": [
          { name: "Аптека 36,6", address: "ул. Зеленая, 8", lat: 55.7504, lng: 37.7147 }
        ],
        "Спорт": [
          { name: "Фитнес-клуб Атлетик", address: "ул. Спортивная, 1", lat: 55.7481, lng: 37.7170 }
        ],
        "Культура": [
          { name: "Дом культуры Прокшино", address: "ул. Театральная, 5", lat: 55.7475, lng: 37.7121 }
        ],
        "Бары": [
          { name: "Бар 'Пенная'", address: "ул. Дружбы, 6", lat: 55.7491, lng: 37.7159 }
        ]
      }
    }
  ];




  const handlePropertyClick = (property) => {
    setSelectedProperty(property);
  };

  return (
    <div className="property-listings">
      <div className="listings-header">
      </div>

      <div className="main-content">
        <div className="listings-container">
          {listings.map((listing) => (
            <PropertyCard 
              key={listing.id} 
              listing={listing} 
              isSelected={selectedProperty?.id === listing.id}
              onClick={() => handlePropertyClick(listing)}
            />
          ))}

        </div>

        <div className="map-sidebar">
        <StaticMap2GIS selectedProperty={selectedProperty} listings={listings} />
        </div>
      </div>
    </div>
  );
};

export default PropertyListings;
