import React from 'react';
import './PropertyCard.css';

const PropertyCard = ({ listing, isSelected, onClick }) => {
  const {
    name,
    description,
    type,
    roomsCnt,
    square,
    floor,
    images,
    url,
    price,
    metrPrice,
    address,
    station
  } = listing;

  return (
    <div 
      className={`property-card ${isSelected ? 'selected' : ''}`}
      onClick={onClick}
      style={{
        border: isSelected ? '2px solid #007aff' : '1px solid #ccc',
        borderRadius: '8px',
        padding: '12px',
        marginBottom: '12px',
        cursor: 'pointer',
        backgroundColor: '#fff'
      }}
    >
      {/* –û—Å–Ω–æ–≤–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è */}
      <div className="property-main-info" style={{ marginBottom: '8px' }}>
        <div style={{ fontWeight: 'bold', fontSize: '16px' }}>{name}</div>
        <div style={{ color: '#007aff', fontWeight: 'bold' }}>{price} <span style={{ fontSize: '12px', color: '#555' }}>({metrPrice})</span></div>
      </div>

      {/* –û–ø–∏—Å–∞–Ω–∏–µ */}
      {description && <div className="property-description" style={{ marginBottom: '8px', fontSize: '14px' }}>{description}</div>}

      {/* –•–∞—Ä–∞–∫—Ç–µ—Ä–∏—Å—Ç–∏–∫–∏ */}
      <div className="property-features" style={{ marginBottom: '8px', fontSize: '14px' }}>
        {type && <span>{type}</span>}
        {roomsCnt && <span> ‚Ä¢ {roomsCnt} {roomsCnt === 1 ? '–∫–æ–º–Ω–∞—Ç–∞' : '–∫–æ–º–Ω–∞—Ç—ã'}</span>}
        {square && <span> ‚Ä¢ {square}</span>}
        {floor && <span> ‚Ä¢ {floor} —ç—Ç–∞–∂</span>}
      </div>

      {/* –ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è */}
      {images && images.length > 0 && (
        <div className="property-images" style={{ display: 'flex', gap: '8px', marginBottom: '8px', overflowX: 'auto' }}>
          {images.map((img, index) => (
            <img 
              key={index}
              src={img}
              alt={`${name} ${index + 1}`}
              style={{ width: '120px', height: '80px', objectFit: 'cover', borderRadius: '4px' }}
            />
          ))}
        </div>
      )}

      {/* –ú–µ—Ç—Ä–æ –∏ –≤—Ä–µ–º—è –ø–µ—à–∫–æ–º */}
      {station && (
        <div className="property-metro" style={{ marginBottom: '4px', fontSize: '14px' }}>
          üöá {station} 
        </div>
      )}

      {/* –ê–¥—Ä–µ—Å */}
      {address && <div className="property-address" style={{ fontSize: '14px', marginBottom: '4px' }}>{address}</div>}

      {/* –°—Å—ã–ª–∫–∞ –Ω–∞ –∏—Å—Ç–æ—á–Ω–∏–∫ */}
      {url && (
        <div className="property-source" style={{ fontSize: '12px', color: '#555' }}>
          <a href={url} target="_blank" rel="noopener noreferrer">–ü–æ–¥—Ä–æ–±–Ω–µ–µ</a>
        </div>
      )}
    </div>
  );
};

export default PropertyCard;
