import React, { useState, useEffect } from 'react';
import './StaticMap2GIS.css';

const StaticMap2GIS = ({ selectedProperty }) => {
  const [mapState, setMapState] = useState({
    size: '1000x380',
    center: '55.75584,37.61782',
    zoom: 14,
    width: 1000,
    height: 380
  });

  const API_KEY = '8f104049-075f-4c47-817b-b8b450854d86';

  const propertyMarkers = [
    { id: 1, lat: 55.5639, lng: 37.4839, type: "Дом", price: "11 000 000 Р" },
    { id: 2, lat: 55.4567, lng: 37.3123, type: "Дом", price: "15 495 200 Р" },
    { id: 3, lat: 55.7890, lng: 37.6789, type: "Квартира", price: "8 500 000 Р" },
    { id: 4, lat: 55.7654, lng: 37.6543, type: "Квартира", price: "12 300 000 Р" }
  ];

  // Обновляем центр карты при выборе объявления
  useEffect(() => {
    if (selectedProperty) {
      setMapState(prev => ({
        ...prev,
        center: `${selectedProperty.lat},${selectedProperty.lng}`,
        zoom: 16
      }));
    }
  }, [selectedProperty]);

  const generateMapUrl = () => {
    const { size, center, zoom } = mapState;
    return `https://static.maps.2gis.com/1.0?s=${size}&c=${center}&z=${zoom}&key=${API_KEY}`;
  };

  const handleZoomIn = () => {
    setMapState(prev => ({
      ...prev,
      zoom: Math.min(18, prev.zoom + 1)
    }));
  };

  const handleZoomOut = () => {
    setMapState(prev => ({
      ...prev,
      zoom: Math.max(1, prev.zoom - 1)
    }));
  };

  const handleReset = () => {
    setMapState({
      size: '880x380',
      center: '55.75584,37.61782',
      zoom: 14
    });
  };

  const handleSizeChange = (newSize) => {
    const [width, height] = newSize.split('x').map(Number);
    setMapState(prev => ({
      ...prev,
      size: newSize,
      width,
      height
    }));
  };

  const handleCustomSizeChange = (dimension, value) => {
    const numValue = parseInt(value) || 0;
    setMapState(prev => {
      const newWidth = dimension === 'width' ? numValue : prev.width;
      const newHeight = dimension === 'height' ? numValue : prev.height;
      return {
        ...prev,
        [dimension]: numValue,
        size: `${newWidth}x${newHeight}`
      };
    });
  };

  const handleCenterChange = (coordinate, value) => {
    setMapState(prev => {
      const [lat, lng] = prev.center.split(',');
      const newLat = coordinate === 'lat' ? parseFloat(value) || 0 : parseFloat(lat);
      const newLng = coordinate === 'lng' ? parseFloat(value) || 0 : parseFloat(lng);
      return {
        ...prev,
        center: `${newLat},${newLng}`
      };
    });
  };

  const handlePresetLocation = (location) => {
    const locations = {
      moscow: { center: '55.75584,37.61782', zoom: 14 },
      spb: { center: '59.9311,30.3609', zoom: 14 },
      ekaterinburg: { center: '56.8431,60.6454', zoom: 14 },
      novosibirsk: { center: '55.0084,82.9357', zoom: 14 }
    };

    const preset = locations[location];
    if (preset) {
      setMapState(prev => ({
        ...prev,
        center: preset.center,
        zoom: preset.zoom
      }));
    }
  };

  return (
    <div className="static-map-2gis">
      <div className="map-header">
        <h3>Карта 2ГИС</h3>
        <div className="map-controls">
          <div className="control-group">
            <label className="control-label">Масштаб:</label>
            <div className="zoom-controls">
              <button 
                className="zoom-btn"
                onClick={handleZoomOut}
                disabled={mapState.zoom <= 1}
                title="Уменьшить"
              >
                −
              </button>
              <span className="zoom-value">{mapState.zoom}</span>
              <button 
                className="zoom-btn"
                onClick={handleZoomIn}
                disabled={mapState.zoom >= 18}
                title="Увеличить"
              >
                +
              </button>
            </div>
          </div>

          <div className="control-group">
            <label className="control-label">Размер:</label>
            <div className="size-controls">
              <input
                type="number"
                value={mapState.width}
                onChange={(e) => handleCustomSizeChange('width', e.target.value)}
                className="size-input"
                min="120"
                max="1280"
                title="Ширина"
              />
              <span className="size-separator">×</span>
              <input
                type="number"
                value={mapState.height}
                onChange={(e) => handleCustomSizeChange('height', e.target.value)}
                className="size-input"
                min="90"
                max="1280"
                title="Высота"
              />
            </div>
          </div>

          <div className="control-group">
            <label className="control-label">Быстрые размеры:</label>
            <div className="preset-sizes">
              <button 
                className="preset-btn"
                onClick={() => handleSizeChange('600x300')}
                title="Маленькая карта"
              >
                600×300
              </button>
              <button 
                className="preset-btn"
                onClick={() => handleSizeChange('1000x380')}
                title="Средняя карта"
              >
                1000×380
              </button>
              <button 
                className="preset-btn"
                onClick={() => handleSizeChange('1200x500')}
                title="Большая карта"
              >
                1200×500
              </button>
            </div>
          </div>

          <div className="control-group">
            <label className="control-label">Координаты:</label>
            <div className="coordinate-controls">
              <input
                type="number"
                step="0.000001"
                value={mapState.center.split(',')[0]}
                onChange={(e) => handleCenterChange('lat', e.target.value)}
                className="coord-input"
                title="Широта"
                placeholder="55.75584"
              />
              <input
                type="number"
                step="0.000001"
                value={mapState.center.split(',')[1]}
                onChange={(e) => handleCenterChange('lng', e.target.value)}
                className="coord-input"
                title="Долгота"
                placeholder="37.61782"
              />
            </div>
          </div>

          <div className="control-group">
            <label className="control-label">Города:</label>
            <div className="location-controls">
              <button 
                className="location-btn"
                onClick={() => handlePresetLocation('moscow')}
                title="Москва"
              >
                Москва
              </button>
              <button 
                className="location-btn"
                onClick={() => handlePresetLocation('spb')}
                title="Санкт-Петербург"
              >
                СПб
              </button>
              <button 
                className="location-btn"
                onClick={() => handlePresetLocation('ekaterinburg')}
                title="Екатеринбург"
              >
                Екб
              </button>
            </div>
          </div>

          <div className="control-group">
            <button 
              className="reset-btn"
              onClick={handleReset}
              title="Сбросить все настройки"
            >
              Сброс
            </button>
          </div>
        </div>
      </div>

      <div className="map-container">
        <img 
          src={generateMapUrl()} 
          alt="Карта 2ГИС"
          className="map-image"
        />

        {/* Маркеры объявлений поверх карты */}
        <div className="markers-overlay">
          {propertyMarkers.map((marker) => {
            // Простое позиционирование маркеров (в реальном приложении нужны точные расчеты)
            const isSelected = selectedProperty?.id === marker.id;
            const x = 20 + (marker.lng - 37.0) * 200; // Примерное позиционирование
            const y = 20 + (55.9 - marker.lat) * 200;

            return (
              <div
                key={marker.id}
                className={`map-marker ${isSelected ? 'selected' : ''}`}
                style={{
                  left: `${Math.max(0, Math.min(100, x))}%`,
                  top: `${Math.max(0, Math.min(100, y))}%`
                }}
                title={`${marker.type}, ${marker.price}`}
              >
                <div className="marker-pin">
                  <div className="marker-icon">
                    {marker.type === 'Дом' ? '🏠' : '🏢'}
                  </div>
                </div>
                {isSelected && (
                  <div className="marker-popup">
                    <div className="popup-content">
                      <div className="popup-title">{marker.type}</div>
                      <div className="popup-price">{marker.price}</div>
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>

      <div className="map-info">
        <div className="info-item">
          <span className="info-label">Масштаб:</span>
          <span className="info-value">{mapState.zoom}</span>
        </div>
        <div className="info-item">
          <span className="info-label">Размер:</span>
          <span className="info-value">{mapState.size}</span>
        </div>
        <div className="info-item">
          <span className="info-label">Центр:</span>
          <span className="info-value">{mapState.center}</span>
        </div>
      </div>
    </div>
  );
};

export default StaticMap2GIS;
