import React from 'react';


// Компонент для отображения недвижимости
export const PropertyCard = ({ property, onClick }) => {
  return (
    <div
      className="property-card"
      style={{ border: "1px solid #ccc", padding: "20px", marginBottom: "20px", cursor: "pointer" }}
      onClick={onClick} // <-- здесь обработчик клика
    >
      <h2>{property.name}</h2>
      <p>{property.description}</p>
      <p><strong>Комнат:</strong> {property.cntrooms}</p>
      <p><strong>Площадь:</strong> {property.square} м²</p>
      <p><strong>Этаж:</strong> {property.floor}</p>
      <p><strong>Адрес:</strong> {property.address}</p>
      <p><strong>Ближайшая станция метро:</strong> {property.station}</p>
      <p><strong>Цена:</strong> {property.price.toLocaleString()} ₽</p>
      <p><strong>Цена за м²:</strong> {property.priceForMetr.toLocaleString()} ₽/м²</p>

      <div className="images" style={{ display: "flex", gap: "10px", overflowX: "auto" }}>
        {property.images.map((img, idx) => (
          <img key={idx} src={img} alt={`Фото ${idx + 1}`} style={{ width: "200px", height: "150px", objectFit: "cover", borderRadius: "8px" }} />
        ))}
      </div>

      <h3>Инфраструктура поблизости:</h3>
      {Object.keys(property.map).map((category) => (
        <div key={category} style={{ marginBottom: "10px" }}>
          <strong>{category}:</strong>
          {property.map[category].length === 0 ? (
            <span> Нет данных</span>
          ) : (
            <ul>
              {property.map[category].map((place, idx) => (
                <li key={idx}>
                  {place.name} — {place.address} ({place.coords[0].toFixed(5)}, {place.coords[1].toFixed(5)})
                </li>
              ))}
            </ul>
          )}
        </div>
      ))}

      <p>
        <a href={property.url} target="_blank" rel="noopener noreferrer">Смотреть на сайте</a>
      </p>
    </div>
  );
};
