import React, { useEffect, useRef } from 'react';

const Map2GISInteractive = ({ selectedProperty }) => {
  const mapRef = useRef(null);

  useEffect(() => {
    if (!window.DG) return;
    const map = DG.map(mapRef.current, {
      center: [55.75584, 37.61782],
      zoom: 14
    });

    if (selectedProperty) {
      // Главный маркер
      DG.marker([selectedProperty.lat, selectedProperty.lng])
        .addTo(map)
        .bindPopup(`${selectedProperty.type}, ${selectedProperty.price}`);

      // Все дополнительные маркеры
      Object.entries(selectedProperty.map || {}).forEach(([cat, places]) => {
        places.forEach(p => {
          DG.marker([p.lat, p.lng]).addTo(map).bindPopup(`${p.name} (${cat})`);
        });
      });

      map.setView([selectedProperty.lat, selectedProperty.lng], 16);
    }

    return () => map.remove();
  }, [selectedProperty]);

  return <div ref={mapRef} style={{ width: '100%', height: '380px' }} />;
};

export default Map2GISInteractive;
