import React, { useState, useEffect, useRef } from 'react';
import './StaticMap2GIS.css';

const StaticMap2GIS = ({ selectedProperty }) => {
  const [mapState, setMapState] = useState({
    size: '880x380',
    center: '55.75584,37.61782',
    zoom: 14
  });
  const mapContainerRef = useRef(null);

  const API_KEY = '8f104049-075f-4c47-817b-b8b450854d86';

  useEffect(() => {
    if (selectedProperty) {
      setMapState(prev => ({
        ...prev,
        center: `${selectedProperty.lat},${selectedProperty.lng}`,
        zoom: 16
      }));
      console.log('✅ Выбрано объявление:', selectedProperty);
    }
  }, [selectedProperty]);

  // Генерация строки маркеров для URL
  const generateMarkersStr = () => {
    if (!selectedProperty) return '';
    const markers = [];

    // Главный маркер
    markers.push(`${selectedProperty.lng},${selectedProperty.lat},pm2blm`);

    // Дополнительные маркеры
    if (selectedProperty.map) {
      Object.entries(selectedProperty.map).forEach(([cat, places]) => {
        places.forEach(p => {
          markers.push(`${p.lng},${p.lat},pm2rdl`);
        });
      });
    }

    // Ограничиваем длину строки маркеров (2ГИС не строит слишком длинные)
    return markers.slice(0, 20).join('|');
  };

  const generateMapUrl = () => {
    const { size, center, zoom } = mapState;
    const markersStr = generateMarkersStr();
    return `https://static.maps.2gis.com/1.0?s=${size}&c=${center}&z=${zoom}&key=${API_KEY}${
      markersStr ? `&markers=${markersStr}` : ''
    }`;
  };

  return (
    <div className="static-map-2gis">
      <div className="map-header">
        <h3>Карта 2ГИС</h3>
      </div>

      <div className="map-container" ref={mapContainerRef}>
        <img
          src={generateMapUrl()}
          alt="Карта 2ГИС"
          className="map-image"
          style={{ width: '100%', height: 'auto' }}
        />
      </div>
    </div>
  );
};

export default StaticMap2GIS;
