/**
 * @deprecated
 */
export type InterpolateExpression<T extends number | string> = [
    'interpolate',
    [
        'linear'
    ] | ['exponential', number],
    [
        'zoom'
    ],
    ...T[]
];
export type ExpressionName = 'interpolate' | 'step' | 'match' | 'gltf-animation';
export type Expression = [ExpressionName, ...any[]];
/**
 * Stub type representing the style layer.
 *
 * Real style layer types will appear a little bit later.
 */
export interface Layer extends Record<string, any> {
}
/**
 * Stub type representing the style.
 *
 * Real style types will appear a little bit later.
 */
export interface Style extends Record<string, any> {
}
/**
 * Style options - paths to icons and fonts.
 */
export interface StyleOptions {
    /**
     * The url to the styles.
     */
    stylePath?: string;
    /**
     * The url to the icons.
     */
    iconsPath: string;
    /**
     * The url to the fonts.
     */
    fontsPath: string;
    /**
     * The url to the models.
     */
    modelsPath: string;
}
/**
 * Config of an original user icon. Only SVG images are supported.
 */
export interface StyleIconConfig {
    /**
     * URL path to an icon:
     *  - absolute: `//external.domain/some_path/some_icon.svg`
     *  - template: `//{appHost}/some_path/some_icon.svg`
     */
    url: string;
}
